package exercice;import java.util.ArrayList;import java.util.List;public class ExerciceSimplicitePire{public static List<String> solution(String s,List<Character> o){String[] m=s.split("[^a-zA-Z0-9]");List<String> mc=new ArrayList<>(),mi=new ArrayList<>();for(int i=0;i<m.length;i++){String t=m[i];if(t.length()>0){if(c(t,o)){boolean ins=false;for(int j=0;j<mc.size();j++){String c=mc.get(j);if(l(t,c,o)<0){mc.add(j,t);ins=true;break;}}if(!ins)mc.add(t); }else{mi.add(t);}}}mc.addAll(mi);return mc;}private static boolean c(String t,List<Character> o){if(o.size()==0)return true;char f=t.charAt(0);return o.contains(f);}private static int l(String m1,String m2,List<Character> o){int l1=m1.length(),l2=m2.length(),l; if(l1>l2)l=l2;else l=l1;for(int i=0;i<l;i++){char c1=m1.charAt(i),c2=m2.charAt(i);if(c1!=c2){int x1=-1,x2=-1;for(int j=0;j<o.size();j++){if(o.get(j)==c1){x1=j;break;}}for(int j=0;j<o.size();j++){if(o.get(j)==c2){x2=j;break;}}if(x1<x2)return -1;else return 1;}}if(l1<l2)return -1;else if(l1>l2)return 1;else return 0;}}
