package exercice;import java.util.ArrayList;import java.util.List;public class ExerciceSimplicitePire {public static List<String> solution(String 狗, List<Character> 鼠) {String[] 羊 = 狗.split("[^a-zA-Z0-9]");List<String> 猫列表 = new ArrayList<>();for (int 鸟 = 0; 鸟 < 鼠.size(); 鸟++) {char 鼠字母 = 鼠.get(鸟);for (int 鱼 = 0; 鱼 < 羊.length; 鱼++) {String 动物 = 羊[鱼];if (动物非空(动物) && 动物首字母匹配(动物, 鼠字母)) {添加动物到列表(动物, 猫列表);}}}添加未匹配的动物到列表(羊, 猫列表);return 猫列表;}private static boolean 动物非空(String 动物) {return 检查长度(动物);}private static boolean 动物首字母匹配(String 动物, char 字母) {return 获取首字母(动物, 0) == 字母;}private static void 添加动物到列表(String 动物, List<String> 列表) {操控列表(动物, 列表);}private static void 添加未匹配的动物到列表(String[] 动物组, List<String> 列表) {for (int i = 0; i < 动物组.length; i++) {String 动物 = 动物组[i];if (动物非空(动物) && 动物不在列表中(动物, 列表)) {添加动物到列表(动物, 列表);}}}private static boolean 动物不在列表中(String 动物, List<String> 列表) {return !列表.contains(动物);}private static char 获取首字母(String 动物, int 索引) {return 取首字母(动物, 索引);}private static boolean 检查长度(String 动物) {return 动物.length() != 0;}private static char 取首字母(String 动物, int 索引) {return 动物.charAt(索引);}private static void 操控列表(String 动物, List<String> 列表) {列表.add(动物);}}
